mapper.select:
SELECT
CONVERT(SUM(IF(t.status_field1 = 1,1,0)),SIGNED) AS '1',
CONVERT(SUM(IF(t.status_field1 = 2,1,0)),SIGNED) AS '2'
FROM sys_archives_library AS sal
INNER JOIN data_archives_library_file AS dalf ON sal.id = dalf.archives_library_id
INNER JOIN ${tableName} AS t ON t.id = dalf.data_id


        AND dalf.archives_library_id=#{archivesLibraryId,jdbcType=INTEGER}


        AND t.status=#{status,jdbcType=SMALLINT}


        AND t.department_id=#{departmentId,jdbcType=INTEGER}


        AND DATE(dalf.create_time) BETWEEN #{startDate,jdbcType=TIMESTAMP} AND #{endDate,jdbcType=TIMESTAMP}

SELECT CONVERT(SUM(IF(t.status = 1,1,0)),SIGNED) AS '1', CONVERT(SUM(IF(t.status = 2,1,0)),SIGNED) AS '2', CONVERT(SUM(IF(t.status = 3,1,0)),SIGNED) AS '3' FROM sys_archives_library AS sal INNER JOIN data_archives_library_file AS dalf ON sal.id = dalf.archives_library_id INNER JOIN ${tableName} AS t ON t.id = dalf.data_id
disease = 1
AND dalf.archives_library_id=#{archivesLibraryId,jdbcType=INTEGER}
AND DATE(dalf.create_time) BETWEEN #{startDate,jdbcType=TIMESTAMP} AND #{endDate,jdbcType=TIMESTAMP}
