mapper.select:
SELECT mb.borrow_type,mb.fileid , mbat.nickname AS mbat_nickname,su.nickname AS su_nickname ,mb.create_time FROM make_borrow AS mb LEFT OUTER JOIN ( SELECT mba.make_borrow_id,su.nickname FROM make_borrow_auditor mba INNER JOIN (SELECT mba.make_borrow_id, mba.sort - 1 AS `last_sort`FROM make_borrow_auditor mba WHERE mba.activate = 1) AS mbat ON mba.make_borrow_id = mbat.make_borrow_id AND mba.sort = mbat.last_sort INNER JOIN sys_user su ON mba.auditor_id = su.id WHERE mba.context IS NOT NULL OR mba.accessory_name IS NOT NULL OR mba.accessory_path IS NOT NULL ) AS mbat ON mb.id = mbat.make_borrow_id INNER JOIN sys_user AS su on mb.user_id = su.id
AND mb.user_type = #{userType,jdbcType=SMALLINT}
ORDER BY mb.create_time LIMIT 7
