mapper.select:
SELECT COUNT(DISTINCT dalfs.user_id) AS `rCount`, COUNT(DISTINCT dalfs.archives_library_file_id) AS `jCount`, COUNT(DISTINCT dalfs.archives_library_file_id) AS `qCount`, FROM sys_archives_library AS sal INNER JOIN data_archives_library_file AS dalf ON sal.id = dalf.archives_library_id RIGHT OUTER JOIN data_archives_library_file_search AS dalfs ON dalf.id = dalfs.archives_library_file_id
AND dalf.archives_library_id=#{archivesLibraryId,jdbcType=INTEGER}
AND dalfs.search_type=#{searchType,jdbcType=SMALLINT}
